from unittest import TestCase
import os
import json
from pathlib import Path
from freezegun import freeze_time
from UC3MTravel import HotelManager
from UC3MTravel import HotelManagementException



class testRoomReservtionTests(TestCase):
    def setUp(self):
        # Crear archivos temporales de prueba para check-in y check-out
        self.check_in = "check_in.json"
        self.check_out = "check_out.json"
        self.nowrite = "no_write.json"
        self.noexiste = "no_existe.json"

    def tearDown(self):
        # Eliminar archivos temporales después de cada prueba
        for file in [self.check_in, self.check_out, self.nowrite, self.noexiste]:
            if Path(file).is_file():
                Path(file).unlink()

    def test1(self):
        "Camino a seguir considerando que no se encuentra el archivo de los check in"
        guest = HotelManager()
        #Se pasa un archivo de check in inexistente
        guest.check_in_file = self.noexiste
        if Path(guest.check_out_file).is_file():
            Path(guest.check_out_file).unlink()
        # Probar la salida
        with self.assertRaises(HotelManagementException) as error:
            guest.guestCheckout("101")
        self.assertEqual(error.exception.message, "Código de habitación no registrado.")


    def test2(self):
        "Camino a seguir considerando que el formato del archivo de los check in no es válido"
        guest = HotelManager()
        guest.check_in_file = self.check_in
        guest.check_out_file = self.check_out
        # Escribir datos inválidos en el archivo de check-in
        with open(guest.check_in_file, "w") as file:
            file.write("")
        # Probar formato JSON inválido para el archivo de check-in
        with self.assertRaises(HotelManagementException) as error:
            guest.guestCheckout("101")
        self.assertEqual(error.exception.message, "JSON Decode Error - Formato JSON no válido")

    def test3(self):
        "Camino a seguir considerando que no se encuentra el código de la habitación"
        guest = HotelManager()
        guest.check_in_file = self.check_in
        guest.check_out_file = self.check_out
        #Se introducen datos en el archivo check in temporal
        with open(guest.check_in_file, "w") as f:
            json.dump([{"_HotelStay__roomkey": "101", "_HotelStay__departure": "14/6/2024"}],f)
        # Probar room_key no registrada
        with self.assertRaises(HotelManagementException) as error:
            guest.guestCheckout("102")
        self.assertEqual(error.exception.message, "Código de habitación no registrado.")

    @freeze_time("13/6/2024")
    def test4(self):
        "Camino a seguir considerando que la fecha de salida no es válida"
        guest = HotelManager()
        guest.check_in_file = self.check_in
        guest.check_out_file = self.check_out
        #Introducir en el check in una fecha no válida
        with open(guest.check_in_file, "w") as f:
            json.dump([{"_HotelStay__roomkey": "101", "_HotelStay__departure": "1704585600."}], f)
        # Probar fecha de salida inválida
        with self.assertRaises(HotelManagementException) as error:
            guest.guestCheckout("101")
        self.assertEqual(error.exception.message, "La fecha de salida no es válida")

    @freeze_time("13/6/2024")
    def test5(self):
        "Camino a seguir considerando que no se encuentra el archivo para los check out"
        guest = HotelManager()
        guest.check_in_file = self.check_in
        guest.check_out_file = self.noexiste
        #Borrar archivo para que sea inexistente
        if Path(guest.check_out_file).is_file():
            Path(guest.check_out_file).unlink()
        #Introducir datos en el check in
        with open(guest.check_in_file, "w") as f:
            json.dump([{"_HotelStay__roomkey": "101", "_HotelStay__departure": "1718236800.0"}], f)
        # Probar archivo de check out inexistente
        with self.assertRaises(HotelManagementException) as error:
            guest.guestCheckout("101")
        self.assertEqual(error.exception.message, "Archivo de salida no encontrado")

    @freeze_time("13/6/2024")
    def test6(self):
        "Camino a seguir considerando que el formato del archivo para los check out no es valido"
        guest = HotelManager()
        guest.check_in_file = self.check_in
        guest.check_out_file = self.check_out
        #Introducir datos en el check in
        with open(guest.check_in_file, "w") as f:
            json.dump([{"_HotelStay__roomkey": "101", "_HotelStay__departure": "1718236800.0"}], f)
        # Escribir datos inválidos en el archivo de check-out
        with open(guest.check_out_file, "w") as file:
            file.write("")
        # Probar formato JSON inválido para el archivo de check-out
        with self.assertRaises(HotelManagementException) as error:
            guest.guestCheckout("101")
        self.assertEqual(error.exception.message, "JSON Decode Error - Formato JSON no válido")

